import React from 'react'
import { Link } from "react-router-dom"
import { useCharContext } from './utils/global.context';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {

  const {state, dispatch} = useCharContext()
  console.log("theme: ", state.theme)

  const [theme, setTheme] = useState("light")

  const themeMode = () => {

    const Theme2 = theme == "dark" ? "light" : "dark"
    setTheme(Theme2)
    dispatch({type: 'CHANGE_THEME', payload: Theme2})

  }

  const body = document.getElementsByClassName("App")

  if(state.theme == "dark") {
  
    body.style.backgroundColor = "#2c3e50"

  }
  else{
    body.style.backgroundColor = "white"
  }
    
  


  
  return (
    <div class="nav">
      <nav>
        <div class="ul-Button">
          <ul>
            <li>
              <Link to="/home">Home</Link>
            </li>
            <li>
              <Link to="/contact">Contact</Link>
            </li>
            <li>
              <Link to="/favs">Favs</Link>
            </li>
          </ul>
          <button class="buttonTheme">{(state.theme === 'dark') ? 'üåô' : '‚òÄÔ∏è'}</button>
        </div>
        
      </nav>
      
    </div>
    
  )
}

export default Navbar
